// @ is an alias to /src
import ReadCRUD from '@/components/ReadCRUD.vue';
import { public_key, ts, hash } from '../marvel.js';
import axios from 'axios';
export default {
  name: 'ReadView',

  data() {
    return {
      chars: []
    };
  },

  components: {
    ReadCRUD
  },
  created: function () {
    console.dir(this.chars);
    console.dir(JSON.parse(JSON.stringify(this.chars)));
    this.listCharacters();
  },
  methods: {
    listCharacters() {
      if (JSON.parse(JSON.stringify(this.chars)).length == 0) {
        console.log("listCharacters");
        axios.get(`https://gateway.marvel.com/v1/public/characters?ts=${ts}&apikey=${public_key}&hash=${hash}`).then(result => {
          console.log(result);
          result.data.data.results.forEach(element => {
            if (!element.description) {
              element.description = "No description available.";
            }

            this.chars.push(element);
          });
        }).catch(error => {
          console.log(error);
        });
      }
    }

  }
};
